  f1: String, f2: Int, f3: Boolean, f4: Double, f5: Long,
  f6: String, f7: Int, f8: Boolean, f9: Double, f10: Long,
  f11: String, f12: Int, f13: Boolean, f14: Double, f15: Long,
  f16: String, f17: Int, f18: Boolean, f19: Double, f20: Long,
  f21: String
