  def fromDynamicValue(value: DynamicValue): Json = value match {
    case DynamicValue.Primitive(pv) => fromPrimitiveValue(pv)
    case DynamicValue.Record(flds) =>
      Object(flds.map { case (k, v) => (k, fromDynamicValue(v)) })
    case DynamicValue.Variant(caseName, v) =>
      Object(Vector("_type" -> String(caseName), "_value" -> fromDynamicValue(v)))
    case DynamicValue.Sequence(elems) =>
      Array(elems.map(fromDynamicValue))
    case DynamicValue.Map(entries) =>
      Array(entries.map { case (k, v) =>
        Object(Vector("key" -> fromDynamicValue(k), "value" -> fromDynamicValue(v)))
      })
  }

  private def fromPrimitiveValue(pv: PrimitiveValue): Json = pv match {
    case PrimitiveValue.Unit              => Null
    case PrimitiveValue.Boolean(v)        => Boolean(v)
    case PrimitiveValue.Byte(v)           => number(v)
    case PrimitiveValue.Short(v)          => number(v)
    case PrimitiveValue.Int(v)            => number(v)
    case PrimitiveValue.Long(v)           => number(v)
    case PrimitiveValue.Float(v)          => number(v)
    case PrimitiveValue.Double(v)         => number(v)
    case PrimitiveValue.Char(v)           => String(v.toString)
    case PrimitiveValue.String(v)         => String(v)
    case PrimitiveValue.BigInt(v)         => number(v)
    case PrimitiveValue.BigDecimal(v)     => number(v)
    case PrimitiveValue.DayOfWeek(v)      => String(v.toString)
    case PrimitiveValue.Duration(v)       => String(v.toString)
    case PrimitiveValue.Instant(v)        => String(v.toString)
    case PrimitiveValue.LocalDate(v)      => String(v.toString)
    case PrimitiveValue.LocalDateTime(v)  => String(v.toString)
    case PrimitiveValue.LocalTime(v)      => String(v.toString)
    case PrimitiveValue.Month(v)          => String(v.toString)
    case PrimitiveValue.MonthDay(v)       => String(v.toString)
    case PrimitiveValue.OffsetDateTime(v) => String(v.toString)
    case PrimitiveValue.OffsetTime(v)     => String(v.toString)
    case PrimitiveValue.Period(v)         => String(v.toString)
    case PrimitiveValue.Year(v)           => String(v.toString)
    case PrimitiveValue.YearMonth(v)      => String(v.toString)
    case PrimitiveValue.ZoneId(v)         => String(v.getId)
    case PrimitiveValue.ZoneOffset(v)     => String(v.toString)
    case PrimitiveValue.ZonedDateTime(v)  => String(v.toString)
    case PrimitiveValue.Currency(v)       => String(v.getCurrencyCode)
    case PrimitiveValue.UUID(v)           => String(v.toString)
  }

  // ===========================================================================
  // KV Interop
  // ===========================================================================

  /**
   * Assembles JSON from a sequence of path-value pairs.
   *
   * {{{
   * Json.fromKV(Seq(
   *   p"a.b" -> Json.number(1),
   *   p"a.c" -> Json.String("x"),
   *   p"d[0]" -> Json.Boolean(true)
   * ))
   * // {"a": {"b": 1, "c": "x"}, "d": [true]}
   * }}}
   *
   * @param kvs The path-value pairs
   * @return Either an error (for conflicting paths) or the assembled JSON
   */
  def fromKV(kvs: Seq[(DynamicOptic, Json)]): Either[JsonError, Json] = ???

  /**
   * Assembles JSON from path-value pairs, throwing on conflict.
   */
  def fromKVUnsafe(kvs: Seq[(DynamicOptic, Json)]): Json = fromKV(kvs).fold(throw _, identity)

  // ===========================================================================
  // Patch Interop
  // ===========================================================================

  /**
   * Serializes a [[JsonPatch]] to its JSON representation.
   *
   * The format follows RFC 6902 (JSON Patch) for standard operations,
   * with extensions for LCS-based sequence diffs.
   *
   * @param patch The patch to serialize
   * @return The JSON representation of the patch
   */
  def fromJsonPatch(patch: JsonPatch): Json = ???

  /**
   * Deserializes a JSON representation into a [[JsonPatch]].
   *
   * @param json The JSON patch representation
   * @return Either an error or the parsed patch
   */
  def toJsonPatch(json: Json): Either[JsonError, JsonPatch] = ???

  // ===========================================================================
  // Ordering
  // ===========================================================================

  /**
   * Ordering for JSON values.
   *
   * Order: Null < Boolean < Number < String < Array < Object
   */
  implicit val ordering: Ordering[Json] = (x: Json, y: Json) => x.compare(y)
}

// =============================================================================
// MERGE STRATEGY
// =============================================================================

/**
 * Strategy for merging JSON values.
 */
sealed trait MergeStrategy

object MergeStrategy {

  /**
   * Automatically determines merge behavior based on value types:
   *  - Objects: deep merge (recurse into matching keys)
   *  - Arrays: concatenate
   *  - Primitives: right wins
   */
  case object Auto extends MergeStrategy

  /**
   * Deep merge for objects; concatenate arrays.
   */
  case object Deep extends MergeStrategy

  /**
   * Shallow merge: right value wins for any key conflict.
   */
  case object Shallow extends MergeStrategy

  /**
   * Concatenate arrays; for objects and primitives, right wins.
   */
  case object Concat extends MergeStrategy

  /**
   * Right value always wins (replacement).
   */
  case object Replace extends MergeStrategy

  /**
   * Custom merge function.
   *
   * @param f A function receiving path and both values, returning merged result
   */
  final case class Custom(f: (DynamicOptic, Json, Json) => Json) extends MergeStrategy
}

// =============================================================================
// STRING INTERPOLATORS
// =============================================================================

/**
 * Provides string interpolators for JSON paths and literals.
 *
 * Import with:
 * {{{
 * import zio.blocks.schema.json.interpolators._
 * }}}
 *
 * ==Path Syntax==
 *
 * The `p` interpolator creates [[DynamicOptic]] paths using a JSONPath-compatible dialect:
 *
 * {{{
 * p"foo.bar"           // fields "foo" then "bar"
 * p"users[0]"          // field "users", then index 0
 * p"users[0].name"     // field "users", index 0, field "name"
 * p"items[*]"          // field "items", then all array elements
 * p"config{*}"         // field "config", then all object values
 * p"config{*:}"        // field "config", then all object keys
 * p"[0,2,5]"           // indices 0, 2, and 5
 * p"[0:5]"             // slice: indices 0 through 4
 * p"[::2]"             // slice: every other element
 * p"`field.name`"      // field with dots in name (backtick escaping)
 * p"""["field"]"""     // alternate field syntax (bracket notation)
 * }}}
 *
 * ===JSONPath Compatibility===
 *
 * This syntax is a dialect of JSONPath (RFC 9535). Most JSONPath expressions work:
 *  - `$.foo.bar` - root prefix is optional and ignored
 *  - `.field`, `["field"]` - field access
 *  - `[n]`, `[*]`, `[m,n]`, `[m:n]` - array access
 *
 * '''Not supported:'''
 *  - `..` (recursive descent)
 *  - `[?()]` (filter expressions)
 *
 * ===Extensions beyond JSONPath:===
 *  - `{*}` - all object values (explicit, vs `[*]` which is array-focused in JSONPath)
 *  - `{*:}` - all object keys (not expressible in standard JSONPath)
 *  - Backtick escaping for field names
 */
object interpolators {

  implicit class JsonPathInterpolator(val sc: StringContext) extends AnyVal {

    /**
     * Creates a [[DynamicOptic]] from a path string at compile time.
     *
     * @return The parsed [[DynamicOptic]]
     */
    def p(args: Any*): DynamicOptic = macro PathMacros.pathInterpolator
  }

  implicit class JsonLiteralInterpolator(val sc: StringContext) extends AnyVal {

    /**
     * Creates a [[Json]] value from a JSON literal at compile time.
     *
     * {{{
     * j"""{"name": "Alice", "age": 30}"""
     * j"[1, 2, 3]"
     * j"null"
     * }}}
     *
     * Interpolated values are converted to JSON:
     * {{{
     * val name = "Bob"
     * val age = 25
     * j"""{"name": $name, "age": $age}"""
     * }}}
     *
     * @return The parsed [[Json]] value
     */
    def j(args: Any*): Json = macro PathMacros.jsonInterpolator
  }
}

/**
 * Macro implementations for string interpolators.
 * 
 * Separate implementations would be needed for Scala 2 and Scala 3.
 */
object PathMacros {
  import scala.reflect.macros.blackbox

  def pathInterpolator(c: blackbox.Context)(args: c.Expr[Any]*): c.Expr[DynamicOptic] = ???

  def jsonInterpolator(c: blackbox.Context)(args: c.Expr[Any]*): c.Expr[Json] = ???
}

// =============================================================================
// PLACEHOLDER TYPES (assumed to exist)
// =============================================================================

/**
 * Represents a JSON Schema for validation.
 *
 * Placeholder - actual implementation TBD.
 */
sealed trait JsonSchema

object JsonSchema {
