     * {{{
     * j"""{"name": "Alice", "age": 30}"""
     * j"[1, 2, 3]"
     * j"null"
     * }}}
     *
     * Interpolated values are converted to JSON:
     * {{{
     * val name = "Bob"
     * val age = 25
     * j"""{"name": $name, "age": $age}"""
     * }}}
     *
     * @return The parsed [[Json]] value
     */
    def j(args: Any*): Json = macro PathMacros.jsonInterpolator
  }
}

/**
 * Macro implementations for string interpolators.
 * 
 * Separate implementations would be needed for Scala 2 and Scala 3.
 */
object PathMacros {
  import scala.reflect.macros.blackbox

  def pathInterpolator(c: blackbox.Context)(args: c.Expr[Any]*): c.Expr[DynamicOptic] = ???

  def jsonInterpolator(c: blackbox.Context)(args: c.Expr[Any]*): c.Expr[Json] = ???
}

