}

suite("Schema toString") {
  test("renders full structure") {
    // Schema {
    //   record Person {
    //     name: String
    //     age: Int
    //   }
    // }
  }
}

suite("Reflect toString") {
  test("renders primitive") {
    // String
  }

  test("renders simple record") {
