## TypeRepr: Type Expressions

The expression language for representing type structures.
```scala
sealed trait TypeRepr {
  /** Substitute type parameters with concrete types */
  def substitute(substitutions: Map[TypeParam, TypeRepr]): TypeRepr =
    TypeRepr.substitute(this, substitutions)
}

object TypeRepr {

  // ==================== Type References ====================

  /** Reference to a type by its TypeId */
  final case class Ref(id: TypeId[? <: AnyKind]) extends TypeRepr

  /** Reference to a type parameter */
  final case class ParamRef(
    param: TypeParam,
    binderDepth: Int = 0  // De Bruijn index: 0 = innermost binder
  ) extends TypeRepr

  /** Path-dependent type: qualifier.Member */
  final case class TypeSelect(
    qualifier: TermPath,
    memberName: String
  ) extends TypeRepr {
    def isStable: Boolean = qualifier.isStable
  }

  /** Type projection (deprecated but exists): Outer#Inner */
  final case class TypeProjection(
    prefix: TypeRepr,
    memberName: String
  ) extends TypeRepr

  // ==================== Type Application ====================

  /** Type constructor application: F[A, B] */
  final case class Applied(
    tycon: TypeRepr,
    args: List[TypeRepr]
  ) extends TypeRepr {
    def arity: Int = args.size
  }

  // ==================== Type Constructors ====================

  /** Type lambda: [X, Y] =>> F[X, Y] */
  final case class TypeLambda(
    params: List[TypeParam],
    body: TypeRepr
  ) extends TypeRepr {
    def arity: Int = params.size
  }

  // ==================== Compound Types ====================

  /** Intersection: A & B & C */
  final case class Intersection(components: List[TypeRepr]) extends TypeRepr {
    require(components.sizeIs >= 2, "Intersection requires at least 2 types")
  }

  object Intersection {
    def apply(left: TypeRepr, right: TypeRepr): Intersection =
      (left, right) match {
        case (Intersection(l), Intersection(r)) => Intersection(l ++ r)
        case (Intersection(l), r) => Intersection(l :+ r)
        case (l, Intersection(r)) => Intersection(l :: r)
        case (l, r) => Intersection(List(l, r))
      }
  }

  /** Union: A | B | C */
  final case class Union(components: List[TypeRepr]) extends TypeRepr {
    require(components.sizeIs >= 2, "Union requires at least 2 types")
  }

  object Union {
    def apply(left: TypeRepr, right: TypeRepr): Union =
      (left, right) match {
        case (Union(l), Union(r)) => Union(l ++ r)
        case (Union(l), r) => Union(l :+ r)
        case (l, Union(r)) => Union(l :: r)
        case (l, r) => Union(List(l, r))
      }
  }

  /** Structural/refinement type: { def foo: Int; type T } */
  final case class Structural(
    parents: List[TypeRepr],
    members: List[Member]
  ) extends TypeRepr

  // ==================== Function Types ====================

  /** Regular function: (A, B) => C */
  final case class Function(
    params: List[TypeRepr],
    result: TypeRepr
  ) extends TypeRepr {
    def arity: Int = params.size
  }

  /** Context function: (A, B) ?=> C */
  final case class ContextFunction(
    params: List[TypeRepr],
    result: TypeRepr
  ) extends TypeRepr

  /** Polymorphic function: [A] => A => A */
  final case class PolyFunction(
    typeParams: List[TypeParam],
    result: TypeRepr
  ) extends TypeRepr

  /** Dependent function: (x: A) => x.T */
  final case class DependentFunction(
    params: List[Param],
    result: TypeRepr  // May contain references to param names
  ) extends TypeRepr

  // ==================== Special Parameter Types ====================

  /** By-name type: => A */
  final case class ByName(underlying: TypeRepr) extends TypeRepr

  /** Repeated/vararg type: A* */
  final case class Repeated(underlying: TypeRepr) extends TypeRepr

  // ==================== Tuple Types ====================

  /** Tuple type with optional labels: (A, B) or (name: String, age: Int) */
  final case class Tuple(elements: List[TupleElement]) extends TypeRepr {
    def arity: Int = elements.size
    def isNamed: Boolean = elements.exists(_.label.isDefined)
    def isPositional: Boolean = elements.forall(_.label.isEmpty)

    def labels: List[Option[String]] = elements.map(_.label)
    def types: List[TypeRepr] = elements.map(_.tpe)
  }

  final case class TupleElement(
    label: Option[String],
    tpe: TypeRepr
  )

  object Tuple {
    def positional(types: TypeRepr*): Tuple = 
      Tuple(types.map(t => TupleElement(None, t)).toList)

    def named(fields: (String, TypeRepr)*): Tuple =
      Tuple(fields.map { case (n, t) => TupleElement(Some(n), t) }.toList)
  }

  // ==================== Singleton & Literal Types ====================

  /** Singleton type: x.type */
  final case class Singleton(path: TermPath) extends TypeRepr {
    def isStable: Boolean = path.isStable
  }

  /** This type: this.type within a class */
  final case class ThisType(owner: TypeId[? <: AnyKind]) extends TypeRepr

  /** Super type reference */
  final case class SuperType(
    thisType: TypeRepr,
    mixinTrait: Option[TypeRepr]
  ) extends TypeRepr

  /** Literal/constant type: 42, "foo", true */
  final case class ConstantType(value: Constant) extends TypeRepr

  // ==================== Match Types ====================

  /** Match type: T match { case P => R; ... } */
  final case class MatchType(
    bound: TypeRepr,
    scrutinee: TypeRepr,
    cases: List[MatchTypeCase]
  ) extends TypeRepr

  final case class MatchTypeCase(
    bindings: List[TypeParam],  // Captured type vars, e.g., `t` in `case Array[t] => t`
    pattern: TypeRepr,
    result: TypeRepr            // Can reference bindings via ParamRef
  )

  // ==================== Bounded/Wildcard Types ====================

  /** Wildcard with bounds: ? <: Upper, ? >: Lower */
  final case class Wildcard(bounds: TypeBounds) extends TypeRepr

  object Wildcard {
    val unbounded: Wildcard = Wildcard(TypeBounds.empty)
    def <:(upper: TypeRepr): Wildcard = Wildcard(TypeBounds.upper(upper))
    def >:(lower: TypeRepr): Wildcard = Wildcard(TypeBounds.lower(lower))
  }

  // ==================== Recursive Types ====================

  /** Recursive type: used for recursive type aliases */
  final case class RecType(body: TypeRepr) extends TypeRepr

  /** Reference to enclosing RecType */
  case object RecThis extends TypeRepr

  // ==================== Annotated Types ====================

  /** Type with annotations: A @ann */
  final case class Annotated(
    underlying: TypeRepr,
    annotations: List[Annotation]
  ) extends TypeRepr

  // ==================== Top, Bottom, and Special Types ====================

  case object AnyType extends TypeRepr
  case object AnyKindType extends TypeRepr
  case object NothingType extends TypeRepr
  case object NullType extends TypeRepr
  case object UnitType extends TypeRepr

  // ==================== Substitution ====================

  def substitute(repr: TypeRepr, subs: Map[TypeParam, TypeRepr]): TypeRepr = {
    def go(r: TypeRepr): TypeRepr = r match {
      case ParamRef(param, 0) => subs.getOrElse(param, r)
      case ParamRef(_, _) => r  // Different binder depth
      case Ref(_) => r
      case Applied(tycon, args) => Applied(go(tycon), args.map(go))
      case TypeLambda(params, body) =>
        // Shift substitutions to account for new binders
        val shifted = subs.map { case (p, t) => p -> shiftBinderDepth(t, 1) }
        TypeLambda(params, substitute(body, shifted))
      case Intersection(comps) => Intersection(comps.map(go))
      case Union(comps) => Union(comps.map(go))
      case Structural(parents, members) =>
        Structural(parents.map(go), members.map(substituteMember(_, subs)))
      case Function(params, result) => Function(params.map(go), go(result))
      case ContextFunction(params, result) => ContextFunction(params.map(go), go(result))
      case PolyFunction(tps, result) => PolyFunction(tps, go(result))
      case DependentFunction(params, result) =>
        DependentFunction(params.map(p => p.copy(tpe = go(p.tpe))), go(result))
      case ByName(u) => ByName(go(u))
      case Repeated(u) => Repeated(go(u))
      case Tuple(elems) => Tuple(elems.map(e => e.copy(tpe = go(e.tpe))))
      case MatchType(bound, scrutinee, cases) =>
        MatchType(go(bound), go(scrutinee), cases.map(substituteMatchCase(_, subs)))
      case Wildcard(bounds) => Wildcard(substituteBounds(bounds, subs))
      case RecType(body) => RecType(go(body))
      case Annotated(underlying, anns) => Annotated(go(underlying), anns)
      case TypeSelect(_, _) | TypeProjection(_, _) | Singleton(_) |
           ThisType(_) | SuperType(_, _) | ConstantType(_) | RecThis |
           AnyType | AnyKindType | NothingType | NullType | UnitType => r
    }
    go(repr)
  }

  private def shiftBinderDepth(repr: TypeRepr, delta: Int): TypeRepr = repr match {
    case ParamRef(param, depth) => ParamRef(param, depth + delta)
    case Applied(tycon, args) => Applied(shiftBinderDepth(tycon, delta), args.map(shiftBinderDepth(_, delta)))
    // ... other cases follow same pattern
    case other => other  // Simplified; full impl would recurse
  }

  private def substituteBounds(bounds: TypeBounds, subs: Map[TypeParam, TypeRepr]): TypeBounds =
    TypeBounds(bounds.lower.map(substitute(_, subs)), bounds.upper.map(substitute(_, subs)))

  private def substituteMatchCase(c: MatchTypeCase, subs: Map[TypeParam, TypeRepr]): MatchTypeCase = {
    // Remove bindings from substitution map (they shadow outer params)
    val shadowedSubs = subs -- c.bindings
    MatchTypeCase(c.bindings, substitute(c.pattern, shadowedSubs), substitute(c.result, shadowedSubs))
  }

  private def substituteMember(m: Member, subs: Map[TypeParam, TypeRepr]): Member = m match {
    case Member.Val(name, tpe, isMutable, targetName, annotations) =>
      Member.Val(name, substitute(tpe, subs), isMutable, targetName, annotations)
    case Member.Def(name, typeParams, paramClauses, result, targetName, annotations) =>
      Member.Def(
        name, typeParams,
        paramClauses.map(substituteParamClause(_, subs)),
        substitute(result, subs),
        targetName, annotations
      )
    case Member.TypeMember(name, typeParams, bounds, alias, annotations) =>
      Member.TypeMember(
        name, typeParams,
        substituteBounds(bounds, subs),
        alias.map(substitute(_, subs)),
        annotations
      )
  }

  private def substituteParamClause(clause: ParamClause, subs: Map[TypeParam, TypeRepr]): ParamClause = {
    def subParam(p: Param): Param = p.copy(tpe = substitute(p.tpe, subs))
    clause match {
      case ParamClause.Regular(params) => ParamClause.Regular(params.map(subParam))
      case ParamClause.Using(params) => ParamClause.Using(params.map(subParam))
      case ParamClause.Implicit(params) => ParamClause.Implicit(params.map(subParam))
    }
  }
}
