```scala
object Examples {
  import TypeRepr.*
  import StandardTypes.*

  // ========== Simple Types ==========

  val intType: TypeRepr = int
  val stringType: TypeRepr = string
  val listOfInt: TypeRepr = list(int)
  val mapStringToInt: TypeRepr = map(string, int)

  // ========== Named Tuple ==========

  // (name: String, age: Int)
  val personTuple: TypeRepr = Tuple.named(
    "name" -> string,
    "age" -> int
  )

  // ========== Type Alias ==========

  // type Age = Int
  val AgeId: TypeId[Int] = TypeId.alias[Int](
    "Age",
    Owner.pkg("myapp"),
    Nil,
    int
  )

  // type StringMap[V] = Map[String, V]
  val V = TypeParam.covariant("V", 0)
  val StringMapId: TypeId[Map[String, *]] = TypeId.alias[Map[String, *]](
    "StringMap",
    Owner.pkg("myapp"),
    List(V),
    Applied(Ref(MapId), List(string, ParamRef(V)))
  )

  // ========== Opaque Type ==========

  // opaque type Email = String
  val EmailId: TypeId[String] = TypeId.opaque[String](
    "Email",
    Owner.pkg("myapp"),
    Nil,
    string
  )

  // ========== Enum ==========

  val ColorId: TypeId[?] = TypeId.nominal(
    "Color",
    Owner.pkg("myapp"),
    Nil,
    TypeDefKind.Enum(List(
      EnumCaseInfo("Red", 0, Nil, isObjectCase = true),
      EnumCaseInfo("Green", 1, Nil, isObjectCase = true),
      EnumCaseInfo("Blue", 2, Nil, isObjectCase = true),
      EnumCaseInfo("RGB", 3, List(
        EnumCaseParam("r", int),
        EnumCaseParam("g", int),
        EnumCaseParam("b", int)
      ), isObjectCase = false)
    ))
  )

  // ========== Match Type ==========

  // type Elem[X] = X match { case String => Char; case Array[t] => t }
  val X = TypeParam.invariant("X", 0)
  val t = TypeParam.invariant("t", 0)

  val ElemId: TypeId[?] = TypeId.alias(
    "Elem",
    Owner.pkg("myapp"),
    List(X),
    MatchType(
      bound = any,
      scrutinee = ParamRef(X),
      cases = List(
        MatchTypeCase(Nil, string, Ref(CharId)),
        MatchTypeCase(
          List(t),
          Applied(Ref(???), List(ParamRef(t))), // Array[t]
          ParamRef(t)
        )
      )
    )
  )

  // ========== Structural Type ==========

  // { def size: Int; val name: String }
  val sizedNamed: TypeRepr = Structural(
    parents = Nil,
    members = List(
      Member.Def("size", Nil, Nil, int),
      Member.Val("name", string)
    )
  )

  // ========== Path-Dependent Type ==========

  // myModule.Inner.T
  val pathDependent: TypeRepr = TypeSelect(
    TermPath(List(
      TermPath.Package("myapp"),
      TermPath.Module("myModule"),
      TermPath.Module("Inner")
    )),
    "T"
  )

  // ========== Intersection & Union ==========

  // String & { def foo: Int }
  val stringWithFoo: TypeRepr = Intersection(
    string,
    Structural(Nil, List(Member.Def("foo", Nil, Nil, int)))
  )

  // String | Int
  val stringOrInt: TypeRepr = Union(string, int)

  // ========== Function Types ==========

  // (Int, String) => Boolean
  val func: TypeRepr = Function(List(int, string), boolean)

  // (Int, String) ?=> Boolean (context function)
  val ctxFunc: TypeRepr = ContextFunction(List(int, string), boolean)

  // [A] => A => A (polymorphic function)
  val polyFunc: TypeRepr = PolyFunction(
    List(TypeParam.invariant("A", 0)),
    Function(List(ParamRef(TypeParam.invariant("A", 0))), ParamRef(TypeParam.invariant("A", 0)))
  )
}
