  val stringMapId: TypeId[Map[String, *]] = alias[Map[String, *]](
    name       = "StringMap",
    owner      = pkgMyApp,
    typeParams = List(V),
    aliased    = Applied(Ref(mapId), List(stringType, ParamRef(V)))
  )

