  // opaque type Email = String
  val emailId: TypeId[String] = opaque[String](
    name           = "Email",
    owner          = pkgMyApp,
    typeParams     = Nil,
    representation = stringType
  )
  val emailType: TypeRepr = Ref(emailId)

