  // ===== Complex types =====

  // Option[List[String]]
  val optionListStringType: TypeRepr =
    Applied(Ref(optionId), List(Applied(Ref(listId), List(stringType))))

  // Map[Email, List[Age]]
  val emailToAgesType: TypeRepr =
    Applied(
      Ref(mapId),
      List(
        Ref(emailId),
        Applied(Ref(listId), List(Ref(ageId)))
