          console.log(`::set-env name=extension_dir_text::${picked}`);
          return { set_env: { extension_dir_text: picked } };
        })();
    continue_on_error: false
    delay_ms: 100
    id: step_2
    tool_name: run_command
  - arguments:
      url: chrome://extensions
    delay_ms: 1000
    id: step_3
